mapper.select:
SELECT B.* , FN_USERNAME(B.USER_ID) AS NAME FROM( SELECT ROWNUM RNUM , A.* FROM( SELECT BD.* FROM TB_BOARD_DOC BD WHERE MAP_ID = #{mapId}
AND TITLE LIKE '%'|| #{searchText} ||'%'
ORDER BY REG_DT DESC ) A WHERE ROWNUM < (#{page} * #{rows}) + 1 )B WHERE RNUM > (#{page} - 1) * #{rows}

mapper.insert:


	select SEQ_BOARD_DOC.NEXTVAL FROM DUAL

INSERT INTO TB_BOARD_DOC(
	TITLE
	,USER_ID
	,MAP_ID
	,CNT_READ
	,DOC_ID
	,REG_DT
	,BOARD_CONTENTS
)VALUES(
	#{title}
	,#{userId}
	,#{mapId}
	, 0
	,#{docId}
	, SYSDATE
	,#{boardContents}			
)



	select SEQ_BOARD_DOC.NEXTVAL FROM DUAL

INSERT INTO TB_BOARD_DOC(
	TITLE
	,USER_ID
	,MAP_ID
	,CNT_READ
	,DOC_ID
	,REG_DT
	,BOARD_CONTENTS
)VALUES(
	#{title}
