	,#{mapId}
	, 0
	,#{docId}
	, SYSDATE
	,#{boardContents}			
)

mapper.update:
UPDATE TB_BOARD_DOC
SET TITLE=#{title}, BOARD_CONTENTS=#{boardContents}
WHERE DOC_ID=#{docId}
