mapper.insert:


	select SEQ_BOARD_DOC.NEXTVAL FROM DUAL

INSERT INTO TB_BOARD_DOC(
	TITLE
	,USER_ID
	,MAP_ID
	,CNT_READ
	,DOC_ID
	,REG_DT
	,BOARD_CONTENTS
)VALUES(
	#{title}
	,#{userId}
	,#{mapId}
	,#{cntRead}
	,#{docId}
	,SYSDATE
	,#{boardContents}			
)

mapper.update:
UPDATE TB_BOARD_DOC
SET TITLE=#{title}, BOARD_CONTENTS=#{boardContents}
WHERE DOC_ID=#{docId}
