mapper.insert:


	select SEQ_BOARD_MAP.NEXTVAL FROM DUAL


INSERT INTO TB_BOARD_MAP(
	MAP_NAME
	,MAP_TYPE
	,FILE_YN
	,COMMENT_YN
	,DEL_YN
	,MAP_ID
	,PAR_MAP_ID
	,BOARD_OREDER
	,REG_DT

)VALUES(
	#{mapName}
	,#{mapType}
	,#{fileYn}
	,#{commentYn}
	,#{delYn}
	,#{mapId}
	,#{parMapId}
	,#{boardOreder}
	,SYSDATE
)

mapper.update:
UPDATE TB_BOARD_MAP
SET MAP_NAME = #{mapName}, BOARD_OREDER=#{boardOreder}, MAP_TYPE=#{mapType}
WHERE MAP_ID = #{mapId}
